fn main() { let foo = Foo { bar: String::from("Y"), }; let foo_from_builder = FooBuilder::new().name(String::from("Y")).build(); println!("foo = {:?}", foo); println!("foo from builfer = {:?}", foo_from_builder);
} #[derive(Debug, PartialEq)]
pub struct Foo { // lots of complicated fields bar : String,
} pub struct FooBuilder { // Probably lots of optional fields. bar: String,
} impl FooBuilder { pub fn new() -> Self { // set the minimally required fields of Foo. Self { bar: String::from("x"), } } pub fn name(mut self, bar: String) -> FooBuilder { // set the name on the builder iteself, // and return the builder by value. self.bar = bar; self } // if we can get away with not consuming the builder here, that is an // advantage. It means we can use the FooBuilder as a template for constructing many Foo. pub fn build(self) -> Foo { // Create a Foo from Foo the FooBuilder, applying all settings in FooBuilder to Foo. Foo { bar: self.bar } }
} // Rust 编程之道. P234
struct Circle { x: f64, y: f64, radius: f64,
} struct CircleBuilder { x: f64, y: f64, radius: f64,
} impl Circle { fn area(&self) -> f64 { std::f64::consts::PI * (self.radius * self.radius) } fn new() -> CircleBuilder { CircleBuilder { x: 0.0, y: 0.0, radius: 1.0, } }
} impl CircleBuilder { fn x(&mut self, coordinate: f64) -> &mut CircleBuilder { self.x = coordinate; self } fn y(&mut self, coordinate: f64) -> &mut CircleBuilder { self.y = coordinate; self } fn radius(&mut self, radius: f64) -> &mut CircleBuilder { self.radius = radius; self } fn build(&self) -> Circle { Circle { x: self.x, y: self.y, radius: self.radius, } }
} fn main() { let c = Circle::new().x(1.0).y(2.0).radius(2.0).build(); println!("area = {:?}", c.area()); println!("c.x = {:?}", c.x); println!("c.y = {:?}", c.y);
}

